import * from dw::core::Arrays
output application/java

fun getProcessedRecords(records) = do {
	var size = sizeOf(records)
    var quantity = if(size is Null) 0 else size * vars.config.deleteCoeff as Number
    var slicedRecords = slice(records, 0, quantity)
    ---
    if((sizeOf(slicedRecords)) > 0)
        slicedRecords map ((item) -> {
            id: item[(vars.config.defineByField as String)],
            operation: 'delete',
            db: item.db
        })
    else []
}

var recordsForSQL = vars.data filter($.db == "SQL") orderBy random()
var recordsForMONGO = vars.data filter($.db == "MONGO") orderBy random()
---
flatten((getProcessedRecords(recordsForMONGO)) + (getProcessedRecords(recordsForSQL)))

%dw 2.0
import * from dw::core::Arrays
import * from dw::core::Objects
import * from dw::core::Strings
output application/java

var definingField = vars.config.defineByField as String
var fieldsToExclude = vars.config.fieldsToExclude as Array
var updateCoeff = vars.config.updateCoeff as Number

fun getRecordsToProcess(db) = do {
    var recordsToDelete = vars.processedData filter ($.operation == 'delete') map $.id
    var recordsForUpdating = vars.data filter 
                            (!(recordsToDelete contains $[(definingField)]) 
                            and 
                            ($.db == db as String))
    ---
    recordsForUpdating
}
// Functions for changing data
var stubs = [0, "__updated__", 3.14]
var days = [|P3D|, |P6D|, |P9D|]
var months = [|P3M|, |P6M|, |P9M|]

fun addDays(date) = date + days[randomInt(sizeOf(days))]
fun addMonths(date) = date + months[randomInt(sizeOf(days))]
fun subtractDays(date) = date - days[randomInt(sizeOf(days))]
fun subtractMonths(date) = date - months[randomInt(sizeOf(days))]

fun increase(n) = n + randomInt(n)
fun decrease(n) = n - randomInt(n)
fun multiply(n) = n * randomInt(n/2)
fun divide(n) = n / randomInt((1 + (n/2)))

fun addToString(str) = str ++ " -VHBC-"
fun capitalizeString(str) = capitalize(str) ++ " -VHBCp-"
fun camelizeString(str) = camelize(str) ++ " -VHBCm-"

var numbersOperations = [increase, decrease, multiply, divide]
var datesOperations = [addDays, addMonths, subtractDays, subtractMonths]
var stringOperations = [addToString, capitalizeString, camelizeString]

fun getNewValue(val) = (
    if(val is Object) do{
        var keys = keysOf(val)
        var key = keys[randomInt(sizeOf(keys))]
        ---
        {(key): getNewValue(val[(key)])}
    }
    else if(val is Array) do{
        var index = randomInt(sizeOf(val))
        ---
        {(index): getNewValue(val[index as Number])}
    }
    else if(val is Boolean) !(val)
    else if(val ~= "true") false
    else if(val ~= "false") true
    else if(val contains 'http') val ++ "/new_path"
    else if(matches(val, "^\d{4}-\d{2}-\d{2}")) 
            datesOperations[randomInt(sizeOf(datesOperations))](val)
    else if(val is String) stringOperations[randomInt(sizeOf(stringOperations))](val)
    else if(val is Number) numbersOperations[randomInt(sizeOf(numbersOperations))](val)
    else if(val is Null) stubs[randomInt(sizeOf(stubs))]
    else null
)

fun getValuesToChange(item) = do {
    var keys = keysOf(item) filter !(fieldsToExclude contains $ as String)
    var randomKey = keys[randomInt(sizeOf(keys))]
    ---
    {
        key: randomKey,
        value: getNewValue(item[(randomKey)])
    }
}

fun getProcessedRecords(db) = do {
    var recordsToProcess = getRecordsToProcess(db) orderBy random()
    var slicedRecords = slice(recordsToProcess, 0, (sizeOf(recordsToProcess) * updateCoeff))
    ---
    slicedRecords map ((item) -> {
        id: item[(definingField)],
        operation: 'update',
        db: item.db,
        valuesToChange: getValuesToChange(item)
    })
}

var updateRecords = flatten(getProcessedRecords("MONGO") + getProcessedRecords('SQL'))
---
flatten(vars.processedData + updateRecords)
